import Axios from "axios";
import Head from "next/head";
import Image from "next/image";
import Carousel from "../components/carousel";
import Navbar from "../components/navbar";
import { api } from "../config/urlapi";

export default function Home({ produk }) {
  return (
    <>
      <Head>
        <title>Create Next App</title>
      </Head>
      <Navbar />
      <Carousel />
      <div className="container my-5">
        <h4 className="text-center">OUR PRODUCT</h4>
        <div className="row mt-5">
          {produk.map((produk, i) => {
            return (
              <div className="col-md-4" key={i}>
                <div className="card">
                  <Image
                    className="card-img-top"
                    src={produk.image}
                    alt={produk.image_title}
                    width="100%"
                    height="200"
                    unoptimized
                  />
                  <div className="card-body">
                    <h5 style={{ fontSize: "16pt" }} className="card-title ">
                      {produk.image_title}
                    </h5>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
      {/* end product */}
      <div className="container my-4">
        <h4 className="text-center">GALERY</h4>
        <div className="row justisy-content-center no-gutters"></div>
      </div>
    </>
  );
}

Home.getInitialProps = async () => {
  let produk = await Axios.get(api + "product");
  let galery = await Axios.get(api + "gallery");
  produk = produk.data.data;
  galery = galery.data.data;
  return {
    produk: produk.length <= 3 ? produk : produk.slice(0, 3),
    produk: produk.length <= 4 ? produk : produk.slice(0, 4),
  };
};
