import { Carousel } from "antd";
const Carousels = ({ jmb }) => {
  return (
    <>
      <Carousel autoplay>
        {jmb.map((data) => {
          <div>
            <img
              className="d-block w-100"
              style={{ width: "100%", height: "100%", maxHeight: "85vh" }}
              src="https://images.unsplash.com/photo-1506744038136-46273834b3fb?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=1080&fit=max&ixid=eyJhcHBfaWQiOjM3Njd9"
              alt="First slide"
            />
          </div>;
        })}
      </Carousel>
    </>
  );
};
Carousel.getInitialProps = async () => {
  let jmb = await Axios.get(api + "image-management/images");

  jmb = jmb.data.data;

  return {
    jmb: jmb,
  };
};
export default Carousels;
