import Axios from "axios";
import { useRouter } from "next/router";
import Navbar from "../../components/navbar";
import { api } from "../../config/urlapi";

const router = useRouter();
const { id } = router.query;
function Detail() {
  return (
    <>
      <Navbar />
      <div className="container my-5">
        <div className="row">
          <div className="col-md-12">
            <div
              style={{
                width: "100%",
                height: "70vh",
                position: "relative",
                borderRadius: "2px",
              }}
              className="thumbs"
            >
              <img
                style={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  borderRadius: "25px",
                }}
                src={artikel.image}
                alt={artikel.article_title}
              />
            </div>
            <br />
            <br />
            <h1>{artikel.article_title}</h1>
            <p
              dangerouslySetInnerHTML={{ __html: artikel.article_content }}
            ></p>
          </div>
        </div>
      </div>
    </>
  );
}

Detail.getInitialProps = async () => {
  let artikel = await Axios.get(api + "artikel/" + id);
  artikel = artikel.data.data;
  return { artikel: artikel };
};

export default Detail;
