import Axios from "axios";
import { message, Popconfirm } from "antd";
import { useRouter } from "next/router";
import Link from "next/link";
import { api } from "../../../config/urlapi";
import Admin from "../../../components/admin";

const jmbMin = ({ image }) => {
  const history = useRouter();

  return (
    <>
      <Admin>
        <Link href="/admin/galery/tambah">
          <button className="btn btn-primary btn-sm">tambah galery</button>
        </Link>
        <div className="row">
          {image.map((data) => {
            return (
              <div className="col-lg-4 col-md-6 mt-4">
                <img
                  className="img-fluid"
                  style={{ width: "100%", height: "25vh" }}
                  src={data.image}
                  alt="gambar iot smk wirabuana"
                />
                <div className="my-3">
                  <button
                    onClick={(e) => {
                      e.preventDefault();
                      history.push("/admin/galery/ubah/" + data.id);
                    }}
                    className="btn btn-primary"
                  >
                    ubah
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </Admin>
    </>
  );
};
jmbMin.getInitialProps = async () => {
  let image = await Axios.get(api + "image-management/images");
  image = image.data.data;
  return {
    image: image,
  };
};
export default jmbMin;
