import Axios from "axios";
import { message, Popconfirm } from "antd";
import Image from "next/image";
import { useRouter } from "next/router";
import Link from "next/link";
import { api } from "../../../config/urlapi";
import Admin from "../../../components/admin";
const galeryMin = ({ galery }) => {
  const history = useRouter();

  function hapus(id) {
    Axios.delete(`${api}article/${id}`, {
      headers: {
        api_token: localStorage.getItem("token"),
      },
    })
      .then((res) => {
        message.success("data berhasil dihapus");
        history.push("/admin/galery");
      })
      .catch((err) => console.log(err));
  }
  return (
    <>
      <Admin>
        <Link href="/admin/galery/tambah">
          <button className="btn btn-primary btn-sm">tambah galery</button>
        </Link>
        <div className="row pt-3 pb-5">
          {galery.map((data, i) => {
            return (
              <div className="col-md-6 col-12 mb-5" key={i}>
                <a target="_blank" href={`/galery/${data.id}`}>
                  <div className="card clickable">
                    <Image
                      className="card-img-top"
                      src={data.image}
                      alt={data.article_title}
                      width="100%"
                      height="200"
                      unoptimized
                    />
                    <div className="card-body">
                      <h5 style={{ fontSize: "16pt" }} className="card-title ">
                        {data.article_title}
                      </h5>
                    </div>
                  </div>
                </a>
                <div className="my-3">
                  <Popconfirm
                    title="yakin ingin menghapus?"
                    onConfirm={() => hapus(data.id)}
                    okText="Yes"
                    cancelText="No"
                  >
                    <button className="btn btn-danger mr-2">hapus</button>
                  </Popconfirm>

                  <button
                    onClick={(e) => {
                      e.preventDefault();
                      history.push("/admin/galery/ubah/" + data.id);
                    }}
                    className="btn btn-primary"
                  >
                    ubah
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </Admin>
    </>
  );
};
galeryMin.getInitialProps = async () => {
  let galery = await Axios.get(api + "gallery");
  galery = galery.data.data;
  return {
    galery: galery,
  };
};
export default galeryMin;
